document.addEventListener("DOMContentLoaded",(function(){let e=document.querySelector(".btn-start");document.querySelector(".btn-stop").addEventListener("click",(()=>{chrome.tabs.query({active:!0,currentWindow:!0},(function(e){chrome.tabs.sendMessage(e[0].id,{action:"stopscript"})}))})),e.addEventListener("click",(()=>{let e=document.querySelector(".delay-action"),t=document.querySelector(".delay-loop"),n=document.querySelector(".so-luong");chrome.tabs.query({active:!0,currentWindow:!0},(function(c){if(e.value<1||t.value<1||n.value<1)chrome.scripting.executeScript({target:{tabId:c[0].id},function:()=>{alert("Không được bỏ trống thông tin")}});else{let r=1e3*Number(e.value),o=1e3*Number(t.value),u=Number(n.value);chrome.tabs.sendMessage(c[0].id,{action:"runscript",da:r,dl:o,sl:u})}}))}))}));